import React from 'react';
import TimerFormField from '../TimerFormField';

class TimerForm extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      isOn: false,
      time: 0,
      start: 0,
      name: null
    }
  }

  submit() {
    if (this.state.time !== 0 && this.state.name !== null) {
      const newValues = Object.assign({}, { time: this.state.time, name: this.state.name })
      this.props.onSubmit(newValues);
    }
  }

  onChangeName(event) {
    this.setState({
      name: event.target.value
    })
  }

  startTimer() {
    if (!this.state.isOn) {
      this.setState({
        isOn: true,
        time: this.state.time,
        start: Date.now() - this.state.time
      })
      this.timer = setInterval(() => this.setState({
        time: Date.now() - this.state.start
      }))
    }
  }

  stopTimer() {
    clearInterval(this.timer)
    this.setState({ isOn: false })
  }

  resetTimer() {
    this.setState({ isOn: false, time: 0 })
  }

  render() {
    return(
      <div className="row">
        <div className="col-3">
        </div>
        <div className="col-6 align-self-center">
          <input
            className="timer-form-input"
            name="name"
            placeholder='Enter Session Name'
            onChange={this.onChangeName.bind(this)}
          />
          <TimerFormField
            time={this.state.time}
            startTimer={this.startTimer.bind(this)}
            stopTimer={this.stopTimer.bind(this)}
            resetTimer={this.resetTimer.bind(this)}
          />
        </div>
        <div className="col-3">
        </div>
        <div className="col-12 align-self-center">
          <button className="btn btn-primary btn-block btn-app btn-app-primary" onClick={this.submit.bind(this)}>
            Save
          </button>
        </div>
      </div>
    )
  }

}

export default TimerForm;
