import React from 'react';
import './timerformfield.css';

class TimerFormField extends React.Component {

  format(time) {
    // Parse time in ms
    let seconds = Math.floor((time / 1000) % 60)
    let minutes = Math.floor((time / (1000 * 60)) % 60)
    let hours = Math.floor((time / (1000 * 60 * 60)) % 24);
    // Convert to HH:MM:SS
    hours = (hours < 10) ? "0" + hours : hours;
    minutes = (minutes < 10) ? "0" + minutes : minutes;
    seconds = (seconds < 10) ? "0" + seconds : seconds;
    return hours + ":" + minutes + ":" + seconds;
  }

  render() {
    return (
      <div className="col-12">
        <div className="row">
          <h3>{this.format(this.props.time)}</h3>
        </div>
        <div className="row">
          <button className="btn btn-primary" onClick={this.props.startTimer}>Start</button>
          <button className="btn btn-primary" onClick={this.props.stopTimer}>Stop</button>
          <button className="btn btn-primary" onClick={this.props.resetTimer}>Reset</button>
        </div>
      </div>
    )
  }

}

export default TimerFormField;
