import React from 'react';
import { reduxForm, Field } from 'redux-form';
import { required } from '../../validators';
import style from './userdetailsform.css';

const UserDetailsForm = props => {
  const { handleSubmit, pristine, submitting } = props;
  return(
    <form>
      <div className="form-group">
        <Field
          name='username'
          type='text'
          label='Username'
          placeholder='Username'
          validate={required}
          component="input"
        />
      </div>
      <div className="form-group">
        <Field
          name='password'
          type='password'
          label='Password'
          placeholder='Password'
          validate={required}
          component="input"
        />
      </div>
      { props.error &&
        <div>{props.error}</div>
      }
      <button className="btn btn-primary btn-app btn-app-primary" disabled={pristine || submitting} onClick={handleSubmit(values => {
        props.onSubmit({
          ...values,
          type: 'login'
        })
      })}>
        Login
      </button>
      <button className="btn btn-primary btn-app btn-app-secondary" disabled={pristine || submitting} onClick={handleSubmit(values => {
        props.onSubmit({
          ...values,
          type: 'register'
        })
      })}>
        Register
      </button>
    </form>
  )
}

export default reduxForm({
  form: 'UserDetailsForm'
})(UserDetailsForm);
